import React, { useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import { useCookies } from "react-cookie";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  //   console.log(username , password)

  const [cookie, setCookie] = useCookies(["access_token"]);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:3001/auth/login", {
        username,
        password,
      });
      console.log("responce", response.data);
      setCookie("access_token", response.data.token);
      window.localStorage.setItem("userID", response.data.userID);
      navigate("/");
    } catch (error) {
      console.log("error", error);
    }
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col-lg-4 offset-4 mt-5"             style={{ border: "2px solid black", borderRadius: "20px", padding:"50px" }}
>                    <h1 className="text-center"> Login Here</h1>

          <form
            onSubmit={handleSubmit}
          >

            <div className="form-group mt-3">
              <lable className="form-input">Username</lable>
              <input
                placeholder="username"
                name="username"
                className="form-control"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
            </div>
            <div className="mt-3">
              <lable className="form-lebal">Password</lable>
              <input
                className="form-control"
                placeholder="password"
                name="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            <div className="text-center mt-4">
              <button className="btn btn-primary">Login</button>
            </div>
            <div className="mt-2">
              <span className="text-center">
                If you are New User then{" "}
                <Link to="/register">Click here to Register</Link>
              </span>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
