import React, { useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";

const Register = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [email, setEmail] = useState("");

  console.log(username, password);

  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("called", username, password);

    try {
      const response = await axios.post("http://localhost:3001/auth/register", {
        username,
        email,
        password,
      });
      alert("registration successful!");

      console.log("responce", response);
      navigate("/login");
      alert("registration successful!");
    } catch (error) {
      console.log("error", error);
    }
    setUsername("");
    setPassword("");
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col-lg-4"></div>
        <div className="col-lg-4 mt-5" style={{border: "2px solid black" , padding: "50px", borderRadius:"5%"}}>
          <form onSubmit={handleSubmit} >
          <h1 className="text-center">Register Here</h1>
            <div className="mt-5 form-group">
              <lable className="form-label">Username</lable>
              <input
                placeholder="username"
                name="username"
                className="form-control"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
            </div>
            <div className="mb-1 mt-3">
              <lable className="form-label">Email</lable>
              <input
              type="email"
                placeholder="email"
                name="password"
                value={password}
                onChange={(e) => setEmail(e.target.value)}
                className="form-control"
              />
            </div>
            <div className="mb-1 mt-3">
              <lable className="form-label">Password</lable>
              <input
              type="password"
                placeholder="password"
                name="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="form-control"
              />
            </div>
            <div className="text-center mt-3">
            <button type="submit" className="btn btn-primary" > Submit</button>
            </div>
          </form>
          <span>If you are alredy registered! <Link to="/login">Click here for Login</Link></span>
        </div>
        <div className="col-lg-4"></div>
      </div>
    </div>
  );
};

export default Register;
